// Funzione per mappare un valore da un intervallo all'altro
function mapValue(value, fromMin, fromMax, toMin, toMax) {
  // Calcola la percentuale del valore nell'intervallo di partenza
  const percentage = (value - fromMin) / (fromMax - fromMin);

  // Applica la percentuale nell'intervallo di destinazione
  const mappedValue = percentage * (toMax - toMin) + toMin;

  // Restituisci il valore mappato
  return mappedValue;
}


